import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Pagination from "react-js-pagination";
class OrderDetail extends Component {
    constructor(props) {
        super(props)
        this.state = {
            id: this.props.location.state.id,
            orders: [],
            vendor:[]
        };
        axios.get('order_list/'+this.state.id)
        .then(res => {
            this.setState({
                vendor: res.data
            });
            console.log(this.state.vendor[0].name)
        })
        .catch((error) => {
        console.log(error);
        })

        axios.get('order_detail/'+this.state.id)
        .then(res => {
            this.setState({
                orders: res.data
            });
            console.log(this.state.orders[0].name)
        })
        .catch((error) => {
        console.log(error);
        })
    }
    render() {
        return (
            <div className="container-scroller">
                <Header/>
                <div className="container-fluid page-body-wrapper">
                    <SideBar/>
                    <div className="main-panel">
                        <div className="content-wrapper">
                            <div className="page-header">
                                <h3 className="page-title">
                                    <span className="page-title-icon bg-gradient-primary text-white mr-2">
                                    <i className="mdi mdi-basket menu-icon" />
                                    </span> Quản Lí Nhà Cung Cấp
                                </h3>
                                <nav aria-label="breadcrumb">
                                    <ul className="breadcrumb">
                                        <li className="breadcrumb-item active" aria-current="page">
                                            <span />Tổng Quan <i className="mdi mdi-alert-circle-outline icon-sm text-primary align-middle" />
                                        </li>
                                    </ul>
                                </nav>
                            </div>
                            <div className="card">
                                    <div className="card-body">
                                        <h4 className="card-title">Thông tin khách hàng</h4>
                                        <p>Họ và tên: {this.state.vendor.name}</p>
                                        <p>Số điện thoại: {this.state.vendor[0].phone}</p>
                                        <p>Địa chỉ: {this.state.vendor[0].address}</p>
                                        <p>Ngày đặt hàng: {this.state.vendor[0].created_at}</p>
                                        <p>Ngày giao hàng: {this.state.vendor[0].order_time}</p>
                                        <p>Tình trạng đơn hàng: {this.state.vendor[0].status}</p>
                                        <p>Ghi chú: {this.state.vendor[0].note}</p>
                                        <p>Tổng số tiền đã thanh toán: 23131</p>
                                        <p>Tổng số tiền phải thanh toán:213131</p>
                                    </div>
                                    <div className="card-body">
                                        <h4 className="card-title">Danh sách sản phẩm đặt mua</h4>
                                        <table className="table table-bordered">
                                            <thead>
                                                <tr>
                                                    <th> STT</th>
                                                    <th> Tên SP</th>
                                                    <th> Giá  </th>
                                                    <th> Số lượng </th>
                                                    <th> Khuyến mãi </th>
                                                    <th> Tổng Tiền </th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {
                                                    this.state.orders.map((item,index)=>(
                                                    <tr className="table-info"key={index}>
                                                        <td>{index+1} </td>
                                                        <td>{item.name}</td>
                                                        <td> {item.price} </td>
                                                        <td> {item.quantity} </td>
                                                        <td> {item.discount} % </td>
                                                        <td> {item.price * item.quantity} </td>
                                                    </tr>
                                                ))
                                                }
                                            </tbody>
                                        </table>
                                    </div>
                                    
                                    
                            </div>
                        </div>
                        <Footer/>
                    </div>
                </div>
            </div>  
        );
    }
}

export default OrderDetail;
