import React, { Component } from 'react';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import SideBar from '../../components/SideBar/SideBar';
import axios from 'axios';
import { Link } from 'react-router-dom';
class VendorDetail extends Component {
    constructor(props) {
        super(props)
        this.state = {
           id: this.props.location.state.id,
           vendor: []
        }

        axios.get('admin_vendor/'+this.state.id)
        .then(res => {
            this.setState({
                vendor: res.data.data
            });
        })
        .catch((error) => {
        console.log(error);
        })
    }
    render() {
        return (
            <main>
                <header className="page-header page-header-compact page-header-light border-bottom bg-white mb-4">
                    <div className="container-xl px-4">
                        <div className="page-header-content">
                            <div className="row align-items-center justify-content-between pt-3">
                                <div className="col-auto mb-3">
                                    <h1 className="page-header-title">
                                    <div className="page-header-icon"><svg xmlns="http://www.w3.org/2000/svg" width={24} height={24} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth={2} strokeLinecap="round" strokeLinejoin="round" className="feather feather-user"><path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" /><circle cx={12} cy={7} r={4} /></svg></div>
                                    Account Settings - Profile
                                    </h1>
                                </div>
                                </div>
                            </div>
                        </div>
                    </header>
                    {/* Main page content*/}
                    <div className="container-xl px-4 mt-4">
                        {/* Account page navigation*/}
                        <nav className="nav nav-borders">
                        <a className="nav-link active ms-0" href="account-profile.html">Profile</a>
                        <a className="nav-link" href="account-billing.html">Billing</a>
                        <a className="nav-link" href="account-security.html">Security</a>
                        <a className="nav-link" href="account-notifications.html">Notifications</a>
                        </nav>
                        <hr className="mt-0 mb-4" />
                        <div className="row">
                        <div className="col-xl-4">
                            {/* Profile picture card*/}
                            <div className="card mb-4 mb-xl-0">
                            <div className="card-header">Profile Picture</div>
                            <div className="card-body text-center">
                                {/* Profile picture image*/}
                                <img className="img-account-profile rounded-circle mb-2" src="assets/img/illustrations/profiles/profile-1.png" alt />
                                {/* Profile picture help block*/}
                                <div className="small font-italic text-muted mb-4">JPG or PNG no larger than 5 MB</div>
                                {/* Profile picture upload button*/}
                                <button className="btn btn-primary" type="button">Upload new image</button>
                            </div>
                            </div>
                        </div>
                        <div className="col-xl-8">
                            {/* Account details card*/}
                            <div className="card mb-4">
                            <div className="card-header">Account Details</div>
                            <div className="card-body">
                                <form>
                                {/* Form Group (username)*/}
                                <div className="mb-3">
                                    <label className="small mb-1" htmlFor="inputUsername">Username (how your name will appear to other users on the site)</label>
                                    <input className="form-control" id="inputUsername" type="text" placeholder="Enter your username" defaultValue="username" />
                                </div>
                                {/* Form Row*/}
                                <div className="row gx-3 mb-3">
                                    {/* Form Group (first name)*/}
                                    <div className="col-md-6">
                                    <label className="small mb-1" htmlFor="inputFirstName">First name</label>
                                    <input className="form-control" id="inputFirstName" type="text" placeholder="Enter your first name" defaultValue="Valerie" />
                                    </div>
                                    {/* Form Group (last name)*/}
                                    <div className="col-md-6">
                                    <label className="small mb-1" htmlFor="inputLastName">Last name</label>
                                    <input className="form-control" id="inputLastName" type="text" placeholder="Enter your last name" defaultValue="Luna" />
                                    </div>
                                </div>
                                {/* Form Row        */}
                                <div className="row gx-3 mb-3">
                                    {/* Form Group (organization name)*/}
                                    <div className="col-md-6">
                                    <label className="small mb-1" htmlFor="inputOrgName">Organization name</label>
                                    <input className="form-control" id="inputOrgName" type="text" placeholder="Enter your organization name" defaultValue="Start Bootstrap" />
                                    </div>
                                    {/* Form Group (location)*/}
                                    <div className="col-md-6">
                                    <label className="small mb-1" htmlFor="inputLocation">Location</label>
                                    <input className="form-control" id="inputLocation" type="text" placeholder="Enter your location" defaultValue="San Francisco, CA" />
                                    </div>
                                </div>
                                {/* Form Group (email address)*/}
                                <div className="mb-3">
                                    <label className="small mb-1" htmlFor="inputEmailAddress">Email address</label>
                                    <input className="form-control" id="inputEmailAddress" type="email" placeholder="Enter your email address" defaultValue="name@example.com" />
                                </div>
                                {/* Form Row*/}
                                <div className="row gx-3 mb-3">
                                    {/* Form Group (phone number)*/}
                                    <div className="col-md-6">
                                    <label className="small mb-1" htmlFor="inputPhone">Phone number</label>
                                    <input className="form-control" id="inputPhone" type="tel" placeholder="Enter your phone number" defaultValue="555-123-4567" />
                                    </div>
                                    {/* Form Group (birthday)*/}
                                    <div className="col-md-6">
                                    <label className="small mb-1" htmlFor="inputBirthday">Birthday</label>
                                    <input className="form-control" id="inputBirthday" type="text" name="birthday" placeholder="Enter your birthday" defaultValue="06/10/1988" />
                                    </div>
                                </div>
                                {/* Save changes button*/}
                                <button className="btn btn-primary" type="button">Save changes</button>
                                </form>
                            </div>
                            </div>
                        </div>
                    </div>
                </div>
            </main>
          
        );
    }
}

export default VendorDetail;
